|sort by TimeGenerated asc, CorrelationId
|summarize dcount(CorrelationId) by bin(TimeGenerated, 1d)

// Code to display active and inactive sessions by computer
// Adding a render piechart will automatically display the consolidated information into a piechart for visual
// Display the Current Active and Inactive sessions by linking to the latest counter values from each table. 
Perf
| where CounterName == "Active Sessions"
| extend ActiveSessions = toint(CounterValue)
| project Computer, ActiveTimeStamp=TimeGenerated, ActiveSessions 
| join kind= inner (
   Perf
   | where CounterName == "Inactive Sessions"
   | extend totalinactivesessions = toint(CounterValue)
   | extend InactiveSessions = totalinactivesessions - 2
   | project Computer, InactiveTimeStamp=TimeGenerated, InactiveSessions  
) on Computer 
| where (ActiveTimeStamp - InactiveTimeStamp) between (0min .. 15s)
| project ActiveSessions, InactiveSessions, bin(ActiveTimeStamp, 5m)
//| summarize (ActiveSessions, InactiveSessions) by Computer, bin(ActiveTimeStamp, 5m)
//| summarize arg_max(ActiveTimeStamp, *) by Computer
//| extend Sessions = pack("Inactive Sessions", InactiveSessions, "Active Sessions", ActiveSessions)

//Find Top processes utilizing CPU
// by finding the machine(s) using over 1% of CPU and 0% avg
// then finding the processes using the CPU
// also finding CPU count of the machines to find the actual percentage of CPU being used
//defining our CPU threshold
let CPUThreshold = 1;
//define time sample rate
let Time = 10m;
//let Computer = {Computer};
//define Count of processes to return
let Count = 10;
//Find instances of total cpu being used above 90% over the last 10 minutes
let TopCPU = Perf
| where Computer == "{Computer}"
| where TimeGenerated > now(-Time)
              and ObjectName == "Processor"
              and CounterName == "% Processor Time"
              and InstanceName == "_Total"
              and CounterValue > CPUThreshold
| project Computer, ObjectName, CounterName, CounterValue, TimeGenerated;
//end query
// find top Processes, excluding _Total and Idle instances, there may be other instances you want to exclude as well
let TopProcess = Perf
| where Computer == "{Computer}"
| where TimeGenerated > now(-Time)
               and CounterName == "% Processor Time"
               and InstanceName != "_Total"
               and InstanceName != "Idle"
| project Computer, ObjectName
              , CounterName, InstanceName
              , CounterValue, TimeGenerated;
// end query
// find CPU count for servers(s)
let FindCPU = Perf
| where Computer == "{Computer}"
| where TimeGenerated >= ago(1h)
| where ObjectName == "Processor"
              and CounterName == "% Processor Time"
              and InstanceName!="_Total"
| sort by InstanceName asc nulls first
| summarize CPUCount = dcount(InstanceName) by Computer;
// end query
//Join all 3 datasets together
FindCPU | join(TopCPU) on Computer 
| join(TopProcess)on Computer
| extend PercentProcessorUsed = CounterValue1 / CPUCount
| summarize avg(PercentProcessorUsed) by Computer, ObjectName
                  , CounterName, CPUCount 
                  , TotalCPU=CounterValue //rename CounterValue to TotalCPU 
                  , Process=ObjectName1 //rename ObjectName1 to Process 
                  , ProcessTime=CounterName1 //rename CounterName1 to ProcessTime 
                  , ProcessName=InstanceName //rename InstanceName to ProcessName 
                  , TimeGenerated
| where Process == "Process"
and avg_PercentProcessorUsed > 0 // only return processes that are using more than 25%
| top Count by avg_PercentProcessorUsed desc
| project Computer, CPUCount
                , ProcessName , avg_PercentProcessorUsed
                , TotalCPU, Process
                , ProcessTime, TimeGenerated

